        angular.forEach($scope._supplierArray, function (data, index) {
          if (data === item.supplierId) {
            $scope._supplierArray.splice(index, 1);
          }
        });

        // 设置标识为false，表示取消此项
        item.handleFlag = false;

        //判断是否可进行下一步
        $scope.isGoNextStep = chkMultipleId($scope._supplierArray) ? true : false;
